mapper.insert:
insert into dimission(emp_ID, dim_date, dim_reason,emp_name,gender,dep_ID,dep_name,pos_ID,pos_name,date,confirm_date )
select (#{emp_ID}, #{dim_date}, #{dim_reason} ,e.emp_name,e.gender,e.dep_ID,dep.dep_name,e.pos_ID,pos.pos_name,e.date,e.confirm_date)
from employee e ;
JOIN department dep ON e.dep_ID = dep.dep_ID ;
JOIN position pos ON e.pos_ID = pos.pos_ID ;
WHERE e.emp_ID = #{emp_ID};

DELETE FROM employee WHERE emp_ID = #{emp_ID};

mapper.select:
SELECT dim.emp_ID, dim.dim_date, dim.dim_reason,
emp.emp_name, emp.gender, emp.dep_ID, dep.dep_name,
emp.pos_ID, pos.pos_name, emp.date, emp.confirm_date
FROM dimission dim
JOIN employee emp ON dim.emp_ID = emp.emp_ID
JOIN department dep ON emp.dep_ID = dep.dep_ID
JOIN position pos ON emp.pos_ID = pos.pos_ID


        AND dim.dim_date BETWEEN #{startDate} AND #{endDate}


        AND dim.dim_date >= #{startDate}


        AND dim.dim_date <= #{endDate}
