mapper.select:
SELECT emp.emp_ID, emp.emp_name,emp.pos_ID,emp.dep_ID,emp.gender,emp.date,emp.confirm_date
FROM employee emp
WHERE emp.emp_ID = #{emp_ID}

select dim.emp_ID,dim.dim_date,dim.dim_reason,dim.emp_name,dim.gender,dim.dep_ID,dim.dep_name,dim.pos_ID,dim.pos_name,dim.date,dim.confirm_date
from dimission dim
WHERE dim.emp_ID = #{emp_ID};

SELECT dim.emp_ID, dim.dim_date, dim.dim_reason,
dim.emp_name, dim.gender, dim.dep_ID, dim.dep_name,
dim.pos_ID, dim.pos_name, dim.date, dim.confirm_date
FROM dimission dim


        AND dim.dim_date BETWEEN #{startDate} AND #{endDate}


        AND dim.dim_date >= #{startDate}


        AND dim.dim_date <= #{endDate}
