mapper.select:
select work_experience, academic_background
from employee
WHERE emp_ID = #{emp_ID};

select emp.emp_ID, emp.emp_name, emp.gender, emp.dep_ID, dep.dep_name, emp.pos_ID, pos.pos_name, emp.date, emp.entermode,emp.birthday,emp.phone,emp.email,emp.address,emp.work_experience,emp.academic_background,emp.emp_type,emp.confirm_date,emp.intern_situation,emp.intern_detail
from employee emp
JOIN department dep ON emp.dep_ID = dep.dep_ID
JOIN position pos ON emp.pos_ID = pos.pos_ID


        AND emp.dep_ID = #{dep_ID} 


        AND emp.pos_ID = #{pos_ID} 


        AND emp.date > #{datea} 


        AND emp.date < #{dateb} 


        AND emp.entermode = #{entermode} 


        AND emp.emp_type = #{emp_type} 


        AND emp.confirm_date > #{confirm_datea} 


        AND emp.confirm_date < #{confirm_dateb} 


        AND emp.intern_situation = #{intern_situation}

mapper.update:
UPDATE employee
emp_name = #{emp_name}, gender = #{gender}, dep_ID = #{dep_ID}, pos_ID = #{pos_ID}, date = #{date}, entermode = #{entermode}, birthday = #{birthday}, phone = #{phone}, email = #{email}, address = #{address}, work_experience = #{work_experience}, academic_background = #{academic_background}, emp_type = #{emp_type}, confirm_date = #{confirm_date}, intern_situation = #{intern_situation}, intern_detail = #{intern_detail}
WHERE emp_ID = #{emp_ID};
