mapper.select:
select mov.mov_ID,mov.emp_ID,emp.emp_name, mov.before_dep, dep1.dep_name as before_depname, mov.after_dep, dep2.dep_name as after_depname,mov.before_pos, pos1.pos_name as before_posname, mov.after_pos, pos2.pos_name as after_posname, mov.mov_date, mov.reason,mov.movtype FROM employmov mov JOIN department dep1 ON mov.before_dep = dep1.dep_ID JOIN department dep2 ON mov.after_dep = dep2.dep_ID JOIN `position` pos1 ON mov.before_pos = pos1.pos_ID JOIN `position` pos2 ON mov.after_pos = pos2.pos_ID JOIN employee emp ON mov.emp_ID = emp.emp_ID
AND mov.emp_ID >= #{emp_ID}
AND mov.mov_date >= #{datea}
AND #{dateb} >= mov.mov_date
AND mov.movtype = #{movtype}
